import s from "./index.module.css" //стилі за допомогою Модулів
import { useState} from "react";

export const Button = () => {
    const [added, setAdded] = useState(false);
    const handleAddToFavorites = () => {
        setAdded(!added);
    };
    return (
    <button className={s.customButton} onClick={onClick={handleAddToFavorites}}>
    {added ? "Added to favourites" : "Add to favourites"}
    </button>
    );
};
