import { Fantasy } from "../../components/films/fantasy";
import { Crime } from "../../components/films/crime";
import { Menu } from "../../features/Menu/Menu";
import { useState } from "react";
import { apiFilms } from "../../features/films/api";
import { getFilms } from "../../features/films/api/index.js";
import "../../components/AddToFavourite/index.module.css"

export const Home = () => {

    const [searchFilm, setSearchFilm] = useState('');
    const handleSearchInputChange = (e) => {
        setSearchFilm(e.target.value);
    };
    const filteredMovies = apiFilms.getFilms().filter((film) => {
        return film.name.toLowerCase().includes(searchFilm.toLowerCase());
    });
    return (
    <>
    <Menu />
    <div>
    <h1>Home</h1>
    <h2>Fantasy</h2>
    <input
    type="text"
    placeholder="Search movies..."
    value={searchFilm}
    onChange={handleSearchInputChange}
    />
    {filteredMovies.map((film) => (
        <div className="film">
        <h3>{el.Title}</h3>
          <img src={el.Poster} alt={el.Title} />
          <p>{el.Year}</p>
        </div>
    ))}
    <Fantasy />
    <h2>Crime</h2>
    <Crime />
    </div>
    </>
    );
};
