import styled from "styled-components";
import { Label } from "../Label/Label";

const StyledInput = styled.input`
    width: 200px;
    height: 100px;
    border: solid black 2px;
`;

const StyledLabel = styled(Label) `
    font-style: italic;
`;

export const Input = () => {
    return (
        <>
        <StyledLabel />
        <CustomInput type="text" />
        </>
    );
};
