import { useState, onClick } from "react";

export const addToFavourite = (props) => {
    const { name, image, desc } = props;
    const [added, setAdded] = useState(false);
    const handleAddToFavorites = () => {
        onAddToFavorites({ name, image, desc });
      };
   
    const handleChangeTextOnButton = () => {
        setAdded(!added);
    };
    return (
    <button className={s.customButton} onClick={handleChangeTextOnButton} onClick={handleAddToFavorites}>
    {added ? "Added to favourites" : "Add to favourites"}
    </button>
    );
};
